mapper.select:
select count(*) from member
		where
			userid = #{userid}

select count(*) from member
	where
		nickname = #{nickname}

select count(*) from member
	where
		email = #{email}

select count(*) from member
	where 
		name = #{name} and email = #{email}

select * from member
	where
		userid = #{userid}
		and
		userpw = #{userpw}

select email from member
	where
		name = #{name}
		and
		email = #{email}

select count(userid) from member
	where
		name = #{name}
		and
		email = #{email}

select count(userid) from member
	where
		userid = #{userid}
			and
		name = #{name}
			and
		email = #{email}
