mapper.select:
		select * from board
			where to_char(start_date, 'yyyy-MM') = #{month}
				or to_char(end_date, 'yyyy-MM') = #{month}
            order by idx

select count(*) from board where region like '%' || #{region} || '%' and region like '%' || #{region_section} || '%' and festival_category like '%' || #{festival_category} || '%'
and start_date >= #{start_date}
and end_date <= #{end_date}
and member = (select idx from member where nickname like '%' || #{fes_keyword} || '%') order by idx desc

select count(*) from board where region like '%' || #{region} || '%' and region like '%' || #{region_section} || '%' and festival_category like '%' || #{festival_category} || '%'
and start_date >= #{start_date}
and end_date <= #{end_date}
and (title like '%' || #{fes_keyword} || '%' or url like '%' || #{fes_keyword} || '%' or place like '%' || #{fes_keyword} || '%' or way_to_come like '%' || #{fes_keyword} || '%' or fee like '%' || #{fes_keyword} || '%' or age_limit like '%' || #{fes_keyword} || '%' or inquire like '%' || #{fes_keyword} || '%' or host_org like '%' || #{fes_keyword} || '%' or content like '%' || #{fes_keyword} || '%') order by idx desc

select * from board where region like '%' || #{region} || '%' and region like '%' || #{region_section} || '%' and festival_category like '%' || #{festival_category} || '%'
and start_date >= #{start_date}
and end_date <= #{end_date}
and member = (select idx from member where nickname like '%' || #{fes_keyword} || '%') order by idx desc offset #{offset} rows fetch next #{per_page} rows only

select * from board where region like '%' || #{region} || '%' and region like '%' || #{region_section} || '%' and festival_category like '%' || #{festival_category} || '%'
and start_date >= #{start_date}
and end_date <= #{end_date}
