mapper.select:
		select * from board
			where to_char(start_date, 'yyyy-MM') = #{month}
				or to_char(end_date, 'yyyy-MM') = #{month}
            order by idx

select * from temp_board
	where member = #{member}
		order by write_date desc

select * from temp_board
	where member = #{member}
		and title = #{title}

mapper.update:
update board set
	count = #{count}
	where idx = #{idx}

update board
	set favorites=#{favorites}
	where idx=#{idx}
